class IconLibrary{constructor(){this.iconData=null,this.currentPack=null,this.currentView="main",this.searchQuery="",this.searchFilter="all",this.numFormatter=new Intl.NumberFormat(navigator.language||"en-US"),this.init()}byId(t){return document.getElementById(t)}setView(t){["packs","icons","results","error","loading"].forEach((e=>{const n=this.byId(e);n&&(e===t?n.style.removeProperty("display"):n.style.display="none")}))}formatNumber(t){return"number"==typeof t?this.numFormatter.format(t):t}escapeHtml(t){const e=document.createElement("div");return e.textContent=t,e.innerHTML}async init(){this.setupEventListeners(),await this.loadIconData()}setupEventListeners(){}async loadIconData(){try{const t=await fetch("icons-data.json");if(!t.ok)throw new Error(`HTTP error! status: ${t.status}`);this.iconData=await t.json(),this.showMainView()}catch(t){this.setView("error")}}showMainView(){this.currentView="main",this.currentPack=null,this.setView("packs");this.byId("search-input").value="",this.searchQuery="",this.renderPacks(),this.updateStats()}renderPacks(){this.byId("packs-grid").innerHTML=this.iconData?.packs.length?this.iconData.packs.map((t=>this.packCardHtml(t))).join(""):"<p>No icon packs found.</p>"}renderIcons(t){this.byId("icons-grid").innerHTML=t?.length?t.map((t=>this.iconCardHtml(t,this.currentPack.path))).join(""):"<p>No icons found.</p>"}}document.addEventListener("DOMContentLoaded",(()=>{window.app=new IconLibrary}));