class IconLibrary{constructor(){this.iconData=null,this.currentPack=null,this.currentView="main",this.searchQuery="",this.searchFilter="all",this.numFormatter=new Intl.NumberFormat(navigator.language||"en-US"),this.init()}byId(t){return document.getElementById(t)}setView(t){["packs","icons","results","error"].forEach((e=>{const n=this.byId(e);n&&(n.style.display=e===t?"block":"none")}))}formatNumber(t){return"number"==typeof t?this.numFormatter.format(t):t}escapeHtml(t){const e=document.createElement("div");return e.textContent=t,e.innerHTML}async init(){this.setupEventListeners(),await this.loadIconData()}setupEventListeners(){}async loadIconData(){}}document.addEventListener("DOMContentLoaded",(()=>{window.app=new IconLibrary}));